import{_ as s,o as a,c as n,b as l,d as p,e as o,a as e,r as c}from"./app.b322fe29.js";const t=JSON.parse('{"title":"图片规范","description":"","frontmatter":{"0":"s","1":"i","2":"d","3":"e","4":"b","5":"a","6":"r","7":"D","8":"e","9":"p","10":"t","11":"h","12":":","13":"3"},"headers":[{"level":2,"title":"图片管理","slug":"图片管理","link":"#图片管理","children":[{"level":3,"title":"assets 和 static 的区别","slug":"assets-和-static-的区别","link":"#assets-和-static-的区别","children":[]},{"level":3,"title":"图片资源存储","slug":"图片资源存储","link":"#图片资源存储","children":[]},{"level":3,"title":"图片资源引用","slug":"图片资源引用","link":"#图片资源引用","children":[]},{"level":3,"title":"图片优化","slug":"图片优化","link":"#图片优化","children":[]}]},{"level":2,"title":"图标管理","slug":"图标管理","link":"#图标管理","children":[{"level":3,"title":"字体图标（单色图标）","slug":"字体图标（单色图标）","link":"#字体图标（单色图标）","children":[]},{"level":3,"title":"图片图标（复色图标）","slug":"图片图标（复色图标）","link":"#图片图标（复色图标）","children":[]}]}],"relativePath":"standard/front-end/07-图片规范.md","lastUpdated":1663311966000}'),r={name:"standard/front-end/07-图片规范.md"},D=p("h1",{id:"图片规范",tabindex:"-1"},[o("图片规范 "),p("a",{class:"header-anchor",href:"#图片规范","aria-hidden":"true"},"#")],-1),i=e('<h2 id="图片管理" tabindex="-1">图片管理 <a class="header-anchor" href="#图片管理" aria-hidden="true">#</a></h2><h3 id="assets-和-static-的区别" tabindex="-1"><code>assets</code> 和 <code>static</code> 的区别 <a class="header-anchor" href="#assets-和-static-的区别" aria-hidden="true">#</a></h3><p>详见 <a href="https://athena0304.gitbooks.io/vue-template-webpack-cn/content/static.html" target="_blank" rel="noreferrer">vuejs 处理静态资产</a></p><h3 id="图片资源存储" tabindex="-1">图片资源存储 <a class="header-anchor" href="#图片资源存储" aria-hidden="true">#</a></h3><p><strong>统一存放在 <code>src/assets/images</code> 文件夹，文件目录如下：</strong></p><div class="language-text"><button class="copy"></button><span class="lang">text</span><pre><code><span class="line"><span style="color:#A6ACCD;">├─images         ----------- 图片</span></span>\n<span class="line"><span style="color:#A6ACCD;">    ├─back       ----------- 背景图片</span></span>\n<span class="line"><span style="color:#A6ACCD;">    ├─banner     ----------- banner图</span></span>\n<span class="line"><span style="color:#A6ACCD;">    ├─default    ----------- 默认图片</span></span>\n<span class="line"><span style="color:#A6ACCD;">    ├─figure     ----------- 配图</span></span>\n<span class="line"><span style="color:#A6ACCD;">    ├─icon       ----------- 图标</span></span>\n<span class="line"><span style="color:#A6ACCD;">    ├─logo       ----------- logo</span></span>\n<span class="line"><span style="color:#A6ACCD;">    ├─nodata     ----------- 无数据图</span></span>\n<span class="line"><span style="color:#A6ACCD;">    ├─other      ----------- 其他图片</span></span>\n<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><h3 id="图片资源引用" tabindex="-1">图片资源引用 <a class="header-anchor" href="#图片资源引用" aria-hidden="true">#</a></h3><p>给图片资源配置一个别名：<code>&#39;~&#39;</code>，便于和其他资源区别开来单独管理</p><div class="language-js"><button class="copy"></button><span class="lang">js</span><pre><code><span class="line"><span style="color:#676E95;">// 基于vue-cli2脚手架，在webpack.base.conf.js添加配置:</span></span>\n<span class="line"><span style="color:#FFCB6B;">alias</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">{</span></span>\n<span class="line"><span style="color:#F07178;">    </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">~</span><span style="color:#89DDFF;">&#39;</span><span style="color:#F07178;">: </span><span style="color:#82AAFF;">resolve</span><span style="color:#F07178;">(</span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">src/assets/images</span><span style="color:#89DDFF;">&#39;</span><span style="color:#F07178;">)</span></span>\n<span class="line"><span style="color:#89DDFF;">}</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#676E95;">// 若基于vue-cli3脚手架，在vue.config.js添加配置:</span></span>\n<span class="line"><span style="color:#FFCB6B;">chainWebpack</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">(</span><span style="color:#A6ACCD;">config</span><span style="color:#89DDFF;">)</span><span style="color:#A6ACCD;"> </span><span style="color:#C792EA;">=&gt;</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">{</span></span>\n<span class="line"><span style="color:#F07178;">    </span><span style="color:#A6ACCD;">config</span><span style="color:#89DDFF;">.</span><span style="color:#A6ACCD;">resolve</span><span style="color:#89DDFF;">.</span><span style="color:#A6ACCD;">alias</span></span>\n<span class="line"><span style="color:#F07178;">        </span><span style="color:#89DDFF;">.</span><span style="color:#82AAFF;">set</span><span style="color:#F07178;">(</span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">~</span><span style="color:#89DDFF;">&#39;</span><span style="color:#89DDFF;">,</span><span style="color:#F07178;"> </span><span style="color:#82AAFF;">resolve</span><span style="color:#F07178;">(</span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">src/assets/images</span><span style="color:#89DDFF;">&#39;</span><span style="color:#F07178;">))</span></span>\n<span class="line"><span style="color:#89DDFF;">}</span></span>\n<span class="line"></span></code></pre></div><p><strong><code>image</code> 标签 <code>src</code> 引入：用于一般配图</strong></p><div class="language-js"><button class="copy"></button><span class="lang">js</span><pre><code><span class="line"><span style="color:#676E95;">// html</span></span>\n<span class="line"><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">img</span><span style="color:#89DDFF;"> :src=&quot;imgXxx&quot; </span><span style="color:#C792EA;">style</span><span style="color:#89DDFF;">=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">width: 200px; height: 200px</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">/&gt;</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#676E95;">// js</span></span>\n<span class="line"><span style="color:#89DDFF;">import</span><span style="color:#A6ACCD;"> imgXxx </span><span style="color:#89DDFF;">from</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">~/Sep18-init/figure-xxx.png</span><span style="color:#89DDFF;">&#39;</span></span>\n<span class="line"></span></code></pre></div><p><strong>样式 <code>background</code> 引入：用于图标</strong></p><div class="language-scss"><button class="copy"></button><span class="lang">scss</span><pre><code><span class="line"><span style="color:#676E95;">//icon.scss</span></span>\n<span class="line"><span style="color:#A6ACCD;">$theicons</span><span style="color:#89DDFF;">:</span></span>\n<span class="line"><span style="color:#A6ACCD;">  </span><span style="color:#89DDFF;">(</span><span style="color:#A6ACCD;">follow</span><span style="color:#89DDFF;">,</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">28</span><span style="color:#89DDFF;">,</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">28</span><span style="color:#89DDFF;">),</span></span>\n<span class="line"><span style="color:#A6ACCD;">  </span><span style="color:#89DDFF;">(</span><span style="color:#A6ACCD;">pay</span><span style="color:#89DDFF;">,</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">40</span><span style="color:#89DDFF;">,</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">40</span><span style="color:#89DDFF;">),</span></span>\n<span class="line"><span style="color:#A6ACCD;">  </span><span style="color:#89DDFF;">(</span><span style="color:#A6ACCD;">myfocus</span><span style="color:#89DDFF;">,</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">44</span><span style="color:#89DDFF;">,</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">44</span><span style="color:#89DDFF;">);</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#89DDFF;">@each</span><span style="color:#A6ACCD;"> $icon, $width, $height </span><span style="color:#89DDFF;">in</span><span style="color:#A6ACCD;"> $theicons </span><span style="color:#89DDFF;">{</span></span>\n<span class="line"><span style="color:#A6ACCD;">  </span><span style="color:#89DDFF;">.</span><span style="color:#FFCB6B;">theicon-</span><span style="color:#89DDFF;">#{</span><span style="color:#A6ACCD;">$icon</span><span style="color:#89DDFF;">}</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">{</span></span>\n<span class="line"><span style="color:#A6ACCD;">    </span><span style="color:#B2CCD6;">display</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> inline-block</span><span style="color:#89DDFF;">;</span></span>\n<span class="line"><span style="color:#A6ACCD;">    </span><span style="color:#B2CCD6;">width</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">#{</span><span style="color:#A6ACCD;">$width</span><span style="color:#89DDFF;">}</span><span style="color:#A6ACCD;">px</span><span style="color:#89DDFF;">;</span></span>\n<span class="line"><span style="color:#A6ACCD;">    </span><span style="color:#B2CCD6;">height</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">#{</span><span style="color:#A6ACCD;">$height</span><span style="color:#89DDFF;">}</span><span style="color:#A6ACCD;">px</span><span style="color:#89DDFF;">;</span></span>\n<span class="line"><span style="color:#A6ACCD;">    </span><span style="color:#B2CCD6;">background</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#82AAFF;">url</span><span style="color:#89DDFF;">(</span><span style="color:#A6ACCD;">../images/icon/#{$icon}.png</span><span style="color:#89DDFF;">)</span><span style="color:#A6ACCD;"> no-repeat</span><span style="color:#89DDFF;">;</span></span>\n<span class="line"><span style="color:#A6ACCD;">    </span><span style="color:#B2CCD6;">background-size</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">100%</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">100%</span><span style="color:#89DDFF;">;</span></span>\n<span class="line"><span style="color:#A6ACCD;">  </span><span style="color:#89DDFF;">}</span></span>\n<span class="line"><span style="color:#89DDFF;">}</span></span>\n<span class="line"></span></code></pre></div><blockquote><p><code>css</code> 中一定要用相对路径，才会被解释为模块依赖，编译生成 <code>base64</code> 编码。所以，将需要用 <code>background</code> 去引用图片的样式，集中放在同一个样式文件（如：<code>icon.scss</code>）进行统一管理。 图片大小尽量控制在 <code>200kb</code> 内。</p></blockquote><h3 id="图片优化" tabindex="-1">图片优化 <a class="header-anchor" href="#图片优化" aria-hidden="true">#</a></h3><p>图片的使用与优化影响整个项目的性能与用户体验，下面有一些规则帮助你做的更好：</p><ul><li>使用合适的尺寸不浪费一像素</li><li>使用合适的图片格式，jepg、webp 等在系统支持的情况下</li><li>使用合适的压缩比率，减小图片体积</li><li>小于 10kb 的图被会转化成 base64，内联在 html 中响应更快</li><li>大图片存储在对象存储或者服务端</li><li>服务端支持图片处理服务时，使用图片处理服务进行优化</li></ul><h2 id="图标管理" tabindex="-1">图标管理 <a class="header-anchor" href="#图标管理" aria-hidden="true">#</a></h2><h3 id="字体图标（单色图标）" tabindex="-1">字体图标（单色图标） <a class="header-anchor" href="#字体图标（单色图标）" aria-hidden="true">#</a></h3><ul><li>设计人员将图标上传到阿里矢量图标库，图标命名由开发人员维护，采用英文语义化命名</li></ul><p><img src="/assets/28a935ea8bc74ccc8eecae9474092564.edcc596f.png" alt="阿里矢量图标库"></p><ul><li>开发将字体图标下载至本地，将相关文件存在 <code>assets/iconfont</code> 文件夹下，并将字体图标样式选择器改写如下：</li></ul><p><img src="/assets/53d44d2e1f6a45cfafb7ebc4cb3e54a8.a51c34aa.png" alt="字体图标文件"></p><ul><li>用法：</li></ul><div class="language-js"><button class="copy"></button><span class="lang">js</span><pre><code><span class="line"><span style="color:#676E95;">// main.js</span></span>\n<span class="line"><span style="color:#89DDFF;">import</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">@/assets/iconfont/iconfont.css</span><span style="color:#89DDFF;">&#39;</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#676E95;">// xxx.vue</span></span>\n<span class="line"><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">i</span><span style="color:#89DDFF;"> </span><span style="color:#C792EA;">class</span><span style="color:#89DDFF;">=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">icon-xxx color-xxx</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">&gt;&lt;/</span><span style="color:#F07178;">i</span><span style="color:#89DDFF;">&gt;</span></span>\n<span class="line"></span></code></pre></div><h3 id="图片图标（复色图标）" tabindex="-1">图片图标（复色图标） <a class="header-anchor" href="#图片图标（复色图标）" aria-hidden="true">#</a></h3><ul><li>图片存储：<code>assets/images/icon</code> 文件夹</li><li>图片转成样式：<a href="http://ui.ucmed.cn/doc/%E9%99%84%E5%BD%95/#icon-scss" target="_blank" rel="noreferrer">sass/icon.scss</a></li><li>用法：</li></ul><div class="language-js"><button class="copy"></button><span class="lang">js</span><pre><code><span class="line"><span style="color:#676E95;">// main.js</span></span>\n<span class="line"><span style="color:#89DDFF;">import</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">@/assets/sass/sass-index.scss</span><span style="color:#89DDFF;">&#39;</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#676E95;">// xxx.vue</span></span>\n<span class="line"><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">i</span><span style="color:#89DDFF;"> </span><span style="color:#C792EA;">class</span><span style="color:#89DDFF;">=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">theicon-xxx</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">&gt;&lt;/</span><span style="color:#F07178;">i</span><span style="color:#89DDFF;">&gt;</span></span>\n<span class="line"></span></code></pre></div>',28);const y=s(r,[["render",function(s,p,o,e,t,r){const y=c("VueJobs");return a(),n("div",null,[D,l(y),i])}]]);export{t as __pageData,y as default};
